mapper.select:
        
        select
        if (
        (select max(approvedAt) from point where memberId = 'zkem123456' group by memberId)
        <
        (select max(approvedAt) from cancelPoint where memberId = 'zkem123456' group by memberId), 'F', 'T'
        ) as cancelPointChk,

        if (
        (select max(approvedAt) from point where memberId = 'zkem123456' group by memberId)
        <
        (select max(regdate) from exchangePoint where memberId = 'zkem123456' group by memberId), 'F', 'T'
        ) as exchangePointChk,

        if (
        (select max(approvedAt) from point where memberId = 'zkem123456' group by memberId)
        <
        (select max(payDate) from pointPay where memberId = 'zkem123456' group by memberId), 'F', 'T'
        ) as pointPayChk
