mapper.select:
select product.memberId, product.productId, question.questionId, question.memberId as questionMemberId, questionTitle, questionContent, question.regdate, question.isAnswer, answer from product inner join question on product.productId = question.productId
left join answer on question.questionId = answer.questionId
where isProductCheck = 1 and product.productId = #{productId} and product.memberId = #{memberId}

mapper.insert:
insert into answer
(
 questionId, memberId, answer
)
values
(
 #{questionId}, #{memberId}, #{answer}
)
